import axios from "axios"
export const IS_DEVELOPMENT = location.hostname === "localhost";
export const IS_PRODUCTION = !IS_DEVELOPMENT;

const API_URL = IS_PRODUCTION ? "" : "http://localhost:3000";

export async function apiCall(path: string, payload: {[key: string]:any})  {
    const res = await fetch(`${API_URL}${path}`, {
        method: "POST",
        headers: {
            "Content-Type": "application/json",
            "x-access-token": localStorage.getItem("token") || ""
        },
        body: JSON.stringify(payload)
    }).then(t => t.json())

    return res;
}

export async function axiosApiCall(path: string, payload: {[key: string]:any})  {
    const config = {
        headers: {
          "Content-Type": "application/json",
          "x-access-token": localStorage.getItem("token") || ""
        }
      }
    return await axios.post(`${API_URL}${path}`, JSON.stringify(payload), config);
}
