import React, {useEffect, useState} from "react";
import Header from "../Header";
import './style.css';


export default function Chat() {

  const [chatMessage, setChatMessage] = useState<string>("");
  const [wsRef, setWsRef] = useState<null | WebSocket>(null);



  function sendMessage() {
      if(wsRef?.readyState !== WebSocket.OPEN) {
        // websocket not connected
        return
      }
      wsRef.send(JSON.stringify({ message: chatMessage}))
  }

  useEffect(() => {
       const ws = new WebSocket("ws://localhost:1338");

       ws.addEventListener("open", ()=> {
        ws.send(JSON.stringify({ status: 'ok'}));
       }, {once: true})

       setWsRef(ws);

       return () => {
         ws.close();
       }

  }, [])

  return (
    <div className="chat">
       <Header></Header>
       <div className="chat__wrapper">
          <h1>Im here in chat page!</h1>
          <TextField  onChange={(e: any) => setChatMessage(e.target.value)} value={chatMessage}  variant="outlined" />
          <Button fullWidth className={buttonclasses.secondary}  onClick={sendMessage} color="default" >Send Message</Button>
       </div>
    </div>
  )
}
