import {createStore} from "redux";


const intitalState = {
    user: {
        email: "",
        uname: ""
    }
}


const reducer: any = (state: any = intitalState, action: any) => {
     const newState:any = state;
     if(action.type === "getUser") {
         newState.user = action.user
     }
     return newState;
}

export const store = createStore(reducer);
