import './style.scss';
import { useHistory } from "react-router-dom"
import logo from "./bellybird.png";
import { store } from "../../redux";
import { Button } from "@material-ui/core";
import { Link } from "react-router-dom";
import { useButtonStyles } from "../../RootStyles";

export default function Header() {
  const history = useHistory();
  const state: any = store.getState();
  const isUserAvailable = state.user.email ? true : false;

  const classes = useButtonStyles();

  function redirectUser() {
    history.push("/");
  }

  function logoutUser() {
    window.localStorage.removeItem("token");
    store.dispatch({ type: 'getUser', user: { email: "", uname: "" } });
    history.push("/");
  }

  return (
    <header className="home__header">
      <h1 className="home__header__heading">Belly Birds</h1>

      <a className="home__header__logo" id="home__header__logo" href="/" onClick={redirectUser}>
        <img src={logo} alt="PhoenixContact" />
      </a>

      {
        isUserAvailable ? (
          <div className="home__header__nav">
            <Button className={classes.secondary} onClick={logoutUser} color="default" >Logout</Button>
            <div className="home__header__nav__username">Hi {state.user.uname}</div>
          </div>) : (<div className="home__header__nav">
            <Button variant="contained" className={classes.primary} component={Link} to="/login" color="primary">Login</Button>
            <Button component={Link} className={classes.secondary} to="/register" color="default" >Register</Button>
          </div>)

      }
    </header>
  )
}
